using Newtonsoft.Json;using System;
using System.ComponentModel.DataAnnotations.Schema;namespace PDM_API.Models{    [Table("WELL_ACTIVITY", Schema = "PDM")]    public class WellActivity    {        [JsonProperty("WELL_BORE_CODE")]        public string WELL_BORE_CODE { get; set; }        [JsonProperty("DAYTIME")]        public DateTime? DAYTIME { get; set; }        [JsonProperty("ID")]        public double? ID { get; set; }        [JsonProperty("PTYPE")]        public string PTYPE { get; set; }
        [JsonProperty("ACTIVITY_CODE")]        public string ACTIVITY_CODE { get; set; }        [JsonProperty("DESCRIPTION")]        public string DESCRIPTION { get; set; }        [JsonProperty("MEMBER_OF")]        public string MEMBER_OF { get; set; }        [JsonProperty("WELLBORE_S")]        public string WELLBORE_S { get; set; }        [JsonProperty("R_WELL_INSTALLATION")]        public string R_WELL_INSTALLATION { get; set; }        [JsonProperty("PSORT")]        public int? PSORT { get; set; }    }}